require &ldquo;openssl&rdquo;
require &ldquo;base64&rdquo;
def des_encrypt(str)
cipher = OpenSSL::Cipher::Cipher.new(&ldquo;des-ecb&rdquo;)
cipher.encrypt
cipher.key = &lsquo;SSOFOUNDER&rsquo;
cipher.random_iv cipher.iv = IV en_str = cipher.update(str)
en_str &lt;&lt; cipher.final
return Base64.encode64(en_str)
end
def des_decrypt(enscrypt_str)
cipher = OpenSSL::Cipher::Cipher.new(&ldquo;des-ecb&rdquo;)
cipher.decrypt
cipher.key = &lsquo;SSOFOUNDER&rsquo;
cipher.random_iv cipher.iv = IV cipher.update(Base64.decode64(enscrypt_str)) + cipher.final
des_str &lt;&lt; cipher.final end

