h1 = {:a =&gt; 1, :b =&gt; 2} #=&gt; {:a=&gt;1, :b=&gt;2}
h2 = {:b =&gt; 3, :d =&gt; 3} #=&gt; {:d=&gt;3, :b=&gt;3}
h1.merge h2 #=&gt; {:a=&gt;1, :d=&gt;3, :b=&gt;3}
h2.merge h1 #=&gt; {:a=&gt;1, :d=&gt;3, :b=&gt;2}
