def aes_encrypt()
cipher = OpenSSL::Cipher::AES.new(128, :CBC)
cipher.encrypt cipher.key = &ldquo;quck7295abvdefgh&rdquo;
data= &lsquo;sssssdsadkjak&rsquo;
cipher.iv = &ldquo;abcdefghhigk&rdquo;
encrypted = cipher.update(data) + cipher.final Base64.strict_encode64(encrypted)
end
def aes_dicrypt()
data = &lsquo;hWmnsBO1buB1==&rsquo;
cipher = OpenSSL::Cipher.new(&lsquo;aes-128-cbc&rsquo;)
cipher.key = &ldquo;quck7295abvdefgh&rdquo;
cipher.iv = &ldquo;abcdefghhigk&rdquo;
cipher.decrypt
plain_text = &ldquo;&rdquo;
plain_text &lt;&lt; cipher.update(Base64.decode64(data))
plain_text &lt;&lt; cipher.final
return plain_text
end
